function B(B, t = !1, i = 1) {
  const n = t ? 1e3 : 1024;
  if (Math.abs(B) < n) return `${B} B`;
  const o = t ? ["kB", "MB", "GB", "TB", "PB", "EB", "ZB", "YB"] : ["KiB", "MiB", "GiB", "TiB", "PiB", "EiB", "ZiB", "YiB"];
  let e = -1;
  const a = Math.pow(10, i);
  do {
    B /= n, ++e;
  } while (Math.round(Math.abs(B) * a) / a >= n && e < o.length - 1);
  return B.toFixed(i) + " " + o[e];
}
